import React, { Component } from 'react'
import logo from './logo.svg'
import Stepper from 'react-stepper-horizontal'
import './App.css'

class App extends Component {
  constructor() {
    super()
    this.state = {
      steps: [
        {
          title: 'Step One',
          href: 'http://example1.com',
          onClick: (e) => {
            e.preventDefault()
            console.log('onClick', 1)
          },
        },
        {
          title: 'Step Two',
          href: 'http://example2.com',
          onClick: (e) => {
            e.preventDefault()
            console.log('onClick', 2)
          },
        },
        {
          title: 'Step Three (Disabled)',
          href: 'http://example3.com',
          onClick: (e) => {
            e.preventDefault()
            console.log('onClick', 3)
          },
        },
        {
          title: 'Step Four',
          href: 'http://example4.com',
          onClick: (e) => {
            e.preventDefault()
            console.log('onClick', 4)
          },
        },
      ],
      currentStep: 0,
    }
    this.onClickNext = this.onClickNext.bind(this)
  }

  onClickNext() {
    const { steps, currentStep } = this.state
    this.setState({
      currentStep: currentStep + 1,
    })
  }
  onClickBack() {
    const { steps, currentStep } = this.state
    this.setState({
      currentStep: currentStep + 1,
    })
  }

  render() {
    const { steps, currentStep } = this.state
    const buttonStyle = {
      background: '#E0E0E0',
      width: 200,
      padding: 16,
      textAlign: 'center',
      margin: '0 auto',
      marginTop: 32,
    }

    return (
      <div>
        <Stepper steps={steps} activeStep={currentStep} disabledSteps={[2]} />
        <div style={buttonStyle} onClick={this.onClickNext}>
          Next
        </div>
        <div style={buttonStyle} onClick={this.onClickBack}>
          Back
        </div>
      </div>
    )
  }
}

export default App
